import React, { useEffect,useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  const Auto = () => {
    const [display,setDisplay] = useState(false);
    const [option, setOption]  = useState([]);
    const [search, setSearch] = useState("");

    useEffect(() => {
      // const api = `https://pokeapi.co/api/v2/pokemon-form/`
      const pokemon =[] 
      const promises = new Array(20)
      .fill().map((v,i) => fetch(`https://pokeapi.co/api/v2/pokemon-form/${i+1}`)
      .then(res => res.json())
      .then(result => console.log(result)));
      Promise.all(promises).then(pokemonArr => {
        return pokemonArr.map(value => 
          value.json())
          .then(({name, sprites:{front_default: sprite }}) => 
          pokemon.push({name, sprite }) )
      })
    }, [])

    return (
      <div  className="flex-container flex-colomn pos-rel">
        <input id="auto" placeholder="type search"/>
      </div>
    )
  }
  return (
    <div className="App">
      <h1>Custom auto complete React</h1>
      <div className="logo"></div>
      <div className="auto-container">
        <Auto/>
        </div>
    </div>
  );
}

export default App;
